const fs = require('fs');
const axios = require('axios');

const doiHeader = 'drg';
const index0 = 51;
const count = 5;
const interval = 5000; // in milliseconds, delay between requests

const baseURL = "https://www.terveysportti.fi/doi/" + doiHeader;

const filename = 'tdoi-' + doiHeader + '.txt';

// https://stackoverflow.com/questions/33418777/write-a-line-into-a-txt-file-with-node-js
const recorder = fs.createWriteStream(filename, {
    flags: 'a' // 'a' means append
});
const writeNewLine = (line) => recorder.write('\n' + line);

// The original atcTruncate function 'return url.match;' was incomplete.
// It needs to be properly implemented if you want to truncate the URL.
// For now, this version returns the full URL.
function atcTruncate (url) {
    // Example: if you wanted to remove query parameters
    // return url.split('?')[0];
    return url; // Currently returns the full URL
}

function drgVisit(nro) {
    return new Promise((resolve) => {
        const numero = ('00000' + nro).slice(-5);

        axios.get(baseURL + numero, {
            // Axios by default considers 2xx status codes as success.
            // Non-2xx responses will be caught by .catch()
            // maxRedirects: 5, // Default is 5, adjust if needed
        })
        .then(response => {
            const finalUrl = response.request.res.responseUrl;
            console.log('Final URL for TDOI drg', numero, ": ", finalUrl);
            writeNewLine(numero + ": " + atcTruncate(finalUrl));
            resolve();
        })
        .catch(error => {
            let statusMessage;
            if (error.response) {
                // The request was made and the server responded with a status code
                // that falls out of the range of 2xx
                statusMessage = `Status ${error.response.status}`;
                console.error('TDOI drg', numero, 'Error:', statusMessage, error.message);
            } else if (error.request) {
                // The request was made but no response was received
                statusMessage = 'No response received from server';
                console.error('TDOI drg', numero, 'Error:', statusMessage, error.message);
            } else {
                // Something happened in setting up the request that triggered an Error
                statusMessage = `Request setup error: ${error.message}`;
                console.error('TDOI drg', numero, 'Error:', statusMessage);
            }
            writeNewLine(numero + ": " + statusMessage);
            resolve(); // Resolve even on error to allow the loop to continue
        });
    });
}

// New delay function that returns a Promise
function promiseDelay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}

async function urlTraverse(alku, maara) {
    console.log(`Starting URL traversal from ${alku} for ${maara} items.`);
    for (let i = alku; i < alku + maara; i++) {
        console.log(`Processing item ${i}...`);
        await drgVisit(i); // Wait for drgVisit to complete

        // If it's not the last item, wait for the specified interval
        if (i < alku + maara - 1) {
            console.log(`Waiting for ${interval}ms before next request...`);
            await promiseDelay(interval);
        }
    }
    console.log("URL traversal complete. Closing file stream.");
    recorder.end(); // Important: Close the write stream when all operations are done
}

// Call the async function
// Using an async IIFE (Immediately Invoked Function Expression)
(async () => {
    try {
        await urlTraverse(index0, count);
        console.log(`Data written to ${filename}`);
    } catch (err) {
        console.error("An error occurred during URL traversal:", err);
        recorder.end(); // Ensure stream is closed on error too
    }
})();
````

