        // 3. CHECK FOR 401/403 STATUS IN THE CATCH BLOCK
        if (error.response && (error.response.status === 401 || error.response.status === 403)) {
            throw new SessionExpiredError(`Received status ${error.response.status}. Session has likely expired.`);
