async function drgVisit(nro) {
    const tdoi = ('00000' + nro).slice(-5);
    const initialUrl = baseURL + tdoi;

    const browser = await puppeteer.launch({ headless: "new" });
    const page = await browser.newPage();

    const navigationHistory = []; // Array to store all visited URLs

    // Set up the event listener BEFORE navigating
    page.on('response', response => {
        navigationHistory.push(response.url());
    });

    try {
        console.log(`Navigating to ${initialUrl} for TDOI ${tdoi}...`);
        // Navigate and wait for the network to be idle, ensuring all redirects have fired
        await page.goto(initialUrl, { waitUntil: 'networkidle0' });

        console.log(`Final page reached: ${page.url()}`);
        console.log('Full navigation history:', navigationHistory);

        // Now, search the history for the URL with the ATC code
        const atcUrl = navigationHistory.find(url => atcRegex.test(url) || koodiRegex.test(url));

        if (atcUrl) {
            const atcCode = atcFind(atcUrl);
            console.log(`Found ATC code "${atcCode}" in history. Fetching Finnish name...`);
            const finnishName = await atcFin(atcCode); // Assuming you have this function
            writeNewLine(`${tdoi}: ${atcCode} = ${finnishName} (from Kela)`);
        } else {
            console.log(`Could not find a valid ATC/LTK code in the navigation history for ${tdoi}.`);
            writeNewLine(`${tdoi}: No valid code found in redirects`);
        }

