async function drgVisit(nro) {
    const tdoi = ('00000' + nro).slice(-5);
    const initialUrl = baseURL + tdoi;

    try {
        // 1. Make the request. Axios will stop at the 200 OK redirect page.
        const response = await axios.get(initialUrl);

        // 2. The HTML content of the redirect page.
        const htmlContent = response.data;

        // 3. Regex to find "window.location.href = '...'" or "window.location.replace('...')"
        const redirectRegex = /(?:window\.location\.href|window\.location\.replace)\s*=\s*['"]([^'"]+)['"]/;
        const match = htmlContent.match(redirectRegex);

        if (match && match[1]) {
            // 4. We found the redirect URL in the script tag!
            const redirectUrl = match[1];
            const atcCode = atcFind(redirectUrl);

            console.log(`TDOI ${tdoi}: Found JS redirect to: ${redirectUrl}`);

            if (atcRegex.test(atcCode) || koodiRegex.test(atcCode)) {
                // 5. Proceed with the ATC code we found
                const finnishName = await atcFin(atcCode); // Assuming you have this function
                writeNewLine(`${tdoi}: ${atcCode} = ${finnishName} (from Kela)`);
            } else {
                writeNewLine(`${tdoi}: Invalid code found in JS redirect: ${atcCode}`);
            }
        } else {
            // 6. The expected redirect script was not found. The site has changed.
            console.error(`TDOI ${tdoi}: Could not find JS redirect in page content. Scraping method may be broken.`);
            writeNewLine(`${tdoi}: Could not find JS redirect`);
