    } catch (error) {
        // This handles actual network errors or non-200 responses for the initial URL
        let statusMessage = `Status ${error.response ? error.response.status : 'N/A'}`;
        console.error('TDOI drg', tdoi, 'Error:', statusMessage, error.message);
