/**
 * Escapes a single field for CSV format according to RFC 4180 rules.
 * @param {any} field The data to be escaped.
 * @returns {string} The CSV-safe string.
 */
function escapeCsvField(field) {
    const fieldStr = String(field); // Ensure it's a string
    // Check if the field contains characters that require quoting
    if (fieldStr.includes(',') || fieldStr.includes('"') || fieldStr.includes('\n')) {
        // Escape double quotes by doubling them, then wrap the whole field in double quotes.
        return `"${fieldStr.replace(/"/g, '""')}"`;
    }
    return fieldStr;
}

/**
 * Fetches one TDOI page, finds the drug code and name,
 * and writes the result as a CSV row to the file stream.
 * @param {string} nro Sequence of drg TDOI
 * @returns {Promise<void>}
 */
async function drgVisit(nro) {
    const tdoi = ('00000' + nro).slice(-5);
    try {
        const response = await axios.get(baseURL + tdoi, {});
        console.log('Final URL for TDOI drg', tdoi, ": ", response.request.res.responseUrl);

        const atcCode = atcFind(response.request.res.responseUrl);
        let drugName = "N/A"; // Default value

        // The /gm flag on regex can be stateful. For simple validation, it's safer without 'g'.
        const atcValidationRegex = /^[A-Z]\d\d/;
        const koodiValidationRegex = /[A-Z]\d([A-Z]+|$)/;

        if (atcValidationRegex.test(atcCode)) {
            drugName = await atcToName(atcCode);
        } else if (koodiValidationRegex.test(atcCode)) {
            drugName = await ltkToName(atcCode, parse(response.data));
        }

        // 1. Create an array of the data fields for the row.
        const rowData = [tdoi, atcCode, drugName];

        // 2. Map each field through the escape function and join with commas.
        const csvLine = rowData.map(escapeCsvField).join(',');

        // 3. Write the single, complete, and properly formatted line.
        writeNewLine(csvLine);

    } catch (error) {
        let statusMessage = `Status ${error.response ? error.response.status : 'N/A'}`;
        console.error('TDOI drg', tdoi, 'Error:', statusMessage);

        // Write the error as a properly formatted CSV row.
        const errorRow = [tdoi, statusMessage, "Error"].map(escapeCsvField).join(',');
        writeNewLine(errorRow);
    }
}

