 * @param {string} atc = ATC code
 * @returns {Promise<string>} English drug name : N/A
 */
async function atcToName(atc) {
    let name = 'ATCDDD N/A';
    try {
        const response = await axios.get(atcBase + atc + atcSimp, {});
        const content = parse(response.data).querySelector('div#content');

        if (!content) {
            return name; // Exit early if the main content div isn't found
        }

        // --- STRATEGY 1: For final substances in a table ---
        // Find a table row <tr> that has a first cell <td> containing the ATC code.
        const tableRow = content.querySelector(`tr:has(td:first-child:contains("${atc}"))`);
        if (tableRow) {
            // If we found the row, get the text from the second cell <td>.
            const nameCell = tableRow.querySelector('td:nth-child(2)');
            if (nameCell) {
                name = nameCell.text.trim(); // .text gets all text from child elements (like <a>)
                console.log('Name for drug/drug group (from table):', atc, ":", name);
                return name; // Return the name we found
            }
        }

        // --- STRATEGY 2: For categories in plain text ---
        // If Strategy 1 failed, try the text-splitting method, but use a more reliable parser feature.
        // .structuredText correctly converts <br> tags into '\n' characters.
        const lines = content.structuredText.split('\n');
        const nameLine = lines.find(line => line.trim().startsWith(atc));

        if (nameLine) {
            // Use substring, which is safer than split for names with multiple words.
            name = nameLine.trim().substring(atc.length).trim();
            console.log('Name for drug/drug group (from text):', atc, ":", name);
        }

    } catch (error) {
        console.log(`Error fetching English name from ATCDDD for code ${atc}:`, error.message);
