async function drgVisit(nro) {
    const tdoi = ('00000' + nro).slice(-5);
    try {
        const response = await axios.get(baseURL + tdoi, {});
        const finalUrl = response.request.res.responseUrl;
        const page = parse(response.data); // Parse the page once at the beginning

        // --- NEW: Detect "No Usage Rights" page ---
        // Replace '#error-message' with the actual selector for the error message container
        const noRightsElement = page.querySelector('#error-message'); 
        if (noRightsElement && noRightsElement.innerText.includes('Kyttoikeus puuttuu')) {
            console.log(`TDOI drg${tdoi}: No usage rights.`);
            const errorLine = [tdoi, atcFind(finalUrl), 'No Usage Rights', ''].map(csvEscape).join(',');
            writeNewLine(errorLine);
            return; // Stop processing this TDOI immediately
        }

        // --- Existing logic for normal pages ---
        console.log('Final URL for TDOI drg', tdoi, ": ", finalUrl);
        const atcCode = atcFind(finalUrl);
        let name = '';
        let nimi = '';

        if (atcRegex.test(atcCode)) {
            name = await atcToName(atcCode);
        }

        // Use the already-parsed page
        nimi = await ltkToName(atcCode, page);

        if (nimi === 'N/A LTK' && atcFinalRegex.test(atcCode)) {
            console.log(`No LTK name found for ATC code ${atcCode}. Trying Kela name instead.`);
            nimi = await atcFin(atcCode);
        }

        const findings = [tdoi, atcCode, name, nimi];
        const csvNewLine = findings.map(csvEscape).join(',');
        writeNewLine(csvNewLine);

    } catch (error) {
        // This block will now correctly handle only true network errors like 404s
        let statusCode = error.response ? error.response.status : 'Request Failed';
        console.error(`TDOI drg${tdoi}: Failed with status ${statusCode}`);
        const errorLine = [tdoi, statusCode, '', ''].map(csvEscape).join(',');
        writeNewLine(errorLine);
    }
}
