/**
 * Fetches a URL using a headless browser to get the fully rendered HTML.
 * @param {string} url The URL to visit.
 * @param {string} cookieString The cookie string from the user.
 * @returns {Promise<string|null>} The full HTML content or null on error.
 */
async function fetchRenderedHTML(url, cookieString) {
    let browser;
    try {
        console.log(`> Launching headless browser for ${url}...`);
        browser = await puppeteer.launch();
        const page = await browser.newPage();

        // Puppeteer needs cookies in a specific object format.
        const cookies = cookieString.split(';').map(pair => {
            const parts = pair.trim().split('=');
            return {
                name: parts[0],
                value: parts.slice(1).join('='), // Handle values that contain '='
                domain: '.terveysportti.fi' // IMPORTANT: Set the correct domain
            };
        });

        await page.setCookie(...cookies);
        // 'networkidle2' waits until there are no more than 2 network connections for 500ms.
        // This is a good way to wait for JS-based content to load.
        await page.goto(url, { waitUntil: 'networkidle2' });

        const content = await page.content();
        return content;
    } catch (e) {
        console.error("Error fetching page with Puppeteer:", e.message);
        return null; // Return null on error
    } finally {
        if (browser) {
            await browser.close();
        }
    }
}


