 * @param {string} filePath The path to the CSV file.
 * @param {string} columnName The name of the column to extract.
 * @returns {Promise<string[]>} A promise that resolves to an array of unique codes.
 */
function getCodesFromCsv(filePath, columnName) {
    return new Promise((resolve, reject) => {
        const codes = new Set(); // Use a Set to automatically handle duplicates
        fs.createReadStream(filePath)
            .pipe(csv())
            .on('data', (row) => {
                if (row[columnName]) {
                    codes.add(row[columnName]);
                }
            })
            .on('end', () => {
                console.log(`Finished reading CSV. Found ${codes.size} unique codes.`);
                resolve(Array.from(codes)); // Convert the Set back to an Array
            })
            .on('error', (error) => {
                reject(error);
            });
    });
}


